package loopsAndConditionalStatement;

public class DoWhileLoop {
	static int num=1;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		int i = 0;
		do {
			System.out.println("i==" + i);
			i++;
			
		}while(i<=5);
		

    	do 
    	{
    		System.out.println(num++);
    	}
    	while(num<=10);

	}

}
